import React, { useState } from 'react';
import "./Header.css"
import image from './browzo.png'
import AccountCircleRoundedIcon from '@mui/icons-material/AccountCircleRounded';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';

const Navbar = () => {
  const [hoveredLink, setHoveredLink] = useState(null);
  const [click, setClick] = useState(false);
  const [dropdown, setDropdown] = useState(false);

  const handleMouseEnter = (link) => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(true);
    }
    setHoveredLink(link);
  };

  const handleMouseLeave = () => {
    setHoveredLink(null);
  };
  const imagestyle={width:"100px"}

  return (
    <nav className="navbar">
        <div className='navbar_image'><img src={image} style={imagestyle}></img></div>
        <div className='navbar-name'>
       <b>BrowZo</b> 
      </div>
      <ul className="navbar-links">
        <li className="navbar-link">
          <a
            href="/"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/' ? '#ffffff' : 'red',
            }}
          >
            Home
          </a>
        </li>
        <li className="navbar-link">
          <a
            href="/products"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/products' ? '#ffffff' : '#000000',
            }}
          >
            Products<KeyboardArrowDownIcon/>
          </a>
        </li>
        <li className="navbar-link">
          <a
            href="/about"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/about' ? '#ffffff' : '#000000',
            }}
          >
            About Us
          </a>
        </li>
        <li className="navbar-link">
          <a
            href="/contact"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/contact' ? '#ffffff' : '#000000',
            }}
          >
            Contact
          </a>
        </li>
        <AccountCircleRoundedIcon/>
      </ul>
      <div className="navbar-slider-underline"></div>
    </nav>
  );
};

export default Navbar;
