import React from 'react'

const LoginForm = () => {
    const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState({});

  const validateName = () => {
    if (name.length < 3) {
      setErrors({ name: "Name must be at least 3 characters long." });
    } else {
      setErrors({ name: "" });
    }
  };

  const validateEmail = () => {
    const regex = new RegExp(/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i);
    if (!regex.test(email)) {
      setErrors({ email: "Invalid email address." });
    } else {
      setErrors({ email: "" });
    }
  };

  const validatePassword = () => {
    const regex = new RegExp(/^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9]).{8,}$/);
    if (!regex.test(password)) {
      setErrors({ password: "Password must be at least 8 characters long and contain at least one uppercase letter and one number." });
    } else {
      setErrors({ password: "" });
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    validateName();
    validateEmail();
    validatePassword();

    if (errors.name || errors.email || errors.password) {
      return;
    }

    // Submit the form data to your API here
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(event) => setName(event.target.value)}
      />
      {errors.name && <p style={{ color: "red" }}>{errors.name}</p>}

      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(event) => setEmail(event.target.value)}
      />
      {errors.email && <p style={{ color: "red" }}>{errors.email}</p>}

      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(event) => setPassword(event.target.value)}
      />
      {errors.password && <p style={{ color: "red" }}>{errors.password}</p>}

      <button type="submit">Register</button>
    </form>
    </div>
  )
}

export default LoginForm
