import React, { useState, useEffect } from 'react';
import { useForm , Controller } from "react-hook-form"
import { TextField,Paper, Button,InputAdornment, Grid,Avatar, Stack, Modal } from '@mui/material';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import EmailOutlinedIcon from '@mui/icons-material/EmailOutlined';
import PersonOutlineOutlinedIcon from '@mui/icons-material/PersonOutlineOutlined';
import CallIcon from '@mui/icons-material/Call';
import axios from 'axios';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import dayjs from 'dayjs';
import { DesktopDatePicker } from '@mui/x-date-pickers/DesktopDatePicker';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import PinDropIcon from '@mui/icons-material/PinDrop';



const Formms = ({ data1 }) => {
  const paperStyle={padding:15,height:'100%',width:"50%", margin:"40px auto"}
  const avatarStyle = { backgroundColor: '#1bbd7e',align:'left' }
  const tomorrow = dayjs(new Date());
  const Swal = require('sweetalert2')
  const { handleSubmit, watch, control, setValue } = useForm({
    mode: "onChange",
  });
  
  const [formData, setFormData] = useState({
    name: '',
    mobile: '',
    email: '',
    joiningDate: '',
    leavingDate: '',
    pincode: '',
    district: '',
    state: '',
    address: '',
  });
  const [data, setData] = useState([]);
  const [errors, setErrors] = useState({});
  const [loadingPincode, setLoadingPincode] = useState(false);

  useEffect(() => {
    if (formData.pincode.length === 6) {
      fetchAddressDetails(formData.pincode);
    }
  }, [formData.pincode]);

  useEffect(()=>{
    if (watch('pincode') && watch('pincode').length==6){
      fetchAddressDetails(watch('pincode'))
    }
  },[watch('pincode')])

  const fetchAddressDetails = async (pincode) => {
    setLoadingPincode(true);
    try {
      const response = await axios.get(`https://api.postalpincode.in/pincode/${pincode}`);
      const data = response.data[0];
      if (data && data.PostOffice && data.PostOffice.length > 0) {
        const postOffice = data.PostOffice[0];
        setFormData({
          ...formData,
          district: postOffice.District,
          state: postOffice.State,
        });
      }
    } catch (error) {
      console.error('Error fetching address details:', error);
    } finally {
      setLoadingPincode(false);
    }
  };


  // const validateForm = () => {
  //   let isValid = true;
  //   const newErrors = {};


  //   if (!/^[a-zA-Z\s]+$/.test(formData.name)) {
  //     newErrors.name = 'Name must contain only alphabets';
  //     isValid = false;
  //   }

    
  //   if (!/^[6789]\d{9}$/.test(formData.mobile)) {
  //     newErrors.mobile = 'Mobile number must be 10 digits and only consist 7,8,9';
