import React, { useState } from 'react';
import "./Header.css"
import image from './browzo.png'
const Navbar = () => {
  const [hoveredLink, setHoveredLink] = useState(null);

  const handleMouseEnter = (link) => {
    setHoveredLink(link);
  };

  const handleMouseLeave = () => {
    setHoveredLink(null);
  };
  const imagestyle={width:"100px"}

  return (
    <nav className="navbar">
        <div className='navbar_image'><img src={image} style={imagestyle}></img></div>
        <div className='navbar-name'>
       <b>BrowZo</b> 
      </div>
      <ul className="navbar-links">
        <li className="navbar-link">
          <a
            href="/"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/' ? '#ffffff' : '#000000',
            }}
          >
            Home
          </a>
        </li>
        <li className="navbar-link">
          <a
            href="/products"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/products' ? '#ffffff' : '#000000',
            }}
          >
            Products
          </a>
        </li>
        <li className="navbar-link">
          <a
            href="/about"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/about' ? '#ffffff' : '#000000',
            }}
          >
            About Us
          </a>
        </li>
        <li className="navbar-link">
          <a
            href="/contact"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/contact' ? '#ffffff' : '#000000',
            }}
          >
            Contact
          </a>
        </li>
      </ul>

      <div className="navbar-slider-underline"></div>
    </nav>
  );
};

export default Navbar;
