import React, { useState } from "react";
import Carousel from "react-bootstrap/Carousel";
import test from './test1.jpg'
import test2 from './test2.jpg'
import test3 from './test3.jpg'


const TestimonialCardSlider = () => {
  const [currentTestimonialIndex, setCurrentTestimonialIndex] = useState(0);

  const handlePreviousButtonClick = () => {
    if (currentTestimonialIndex > 0) {
      setCurrentTestimonialIndex(currentTestimonialIndex - 1);
    }
  };

  const handleNextButtonClick = () => {
    if (currentTestimonialIndex < testimonials.length - 1) {
      setCurrentTestimonialIndex(currentTestimonialIndex + 1);
    }
  };

  const testimonials = [
    {
      author: "John Doe",
      testimonial: "This is the best product I've ever used!",
    },
    {
      author: "Jane Doe",
      testimonial: "This product has changed my life!",
    },
    {
      author: "Peter Parker",
      testimonial: "I can't imagine my life without this product!",
    },
  ];

  return (
    <Carousel>
      {testimonials.map((testimonial, index) => (
        <Carousel.Item key={index}>
          <div className="testimonial-card">
            <img src={test} alt={testimonial.author} />
            <p>{testimonial.testimonial}</p>
          </div>
        </Carousel.Item>
      ))}
      <Carousel.Control
        direction="prev"
        onClick={handlePreviousButtonClick}
      />
      <Carousel.Control
        direction="next"
        onClick={handleNextButtonClick}
      />
    </Carousel>
  );
};

export default TestimonialCardSlider;
