import React, { useState } from 'react';
import "./Header.css"

const Navbar = () => {
  const [hoveredLink, setHoveredLink] = useState(null);

  const handleMouseEnter = (link) => {
    setHoveredLink(link);
  };

  const handleMouseLeave = () => {
    setHoveredLink(null);
  };

  return (
    <nav className="navbar">
      <div className='navbar-name'>
        BrowZo
      </div>
      <ul className="navbar-links">
        <li className="navbar-link">
          <a
            href="/"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/' ? '#ffffff' : '#000000',
            }}
          >
            Home
          </a>
        </li>
        <li className="navbar-link">
          <a
            href="/products"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/products' ? '#ffffff' : '#000000',
            }}
          >
            Products
          </a>
        </li>
        <li className="navbar-link">
          <a
            href="/about"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/about' ? '#ffffff' : '#000000',
            }}
          >
            About Us
          </a>
        </li>
        <li className="navbar-link">
          <a
            href="/contact"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{
              color: hoveredLink === '/contact' ? '#ffffff' : '#000000',
            }}
          >
            Contact
          </a>
        </li>
      </ul>

      <div className="navbar-slider-underline"></div>
    </nav>
  );
};

export default Navbar;
